import logo from './logo.svg';
import './App.css';
import {Routes, Route} from "react-router-dom";
import React, {useState} from "react";
import PhotoForm from "./pages/PhotoForm";
import PhotoView from "./pages/PhotoView";


function App() {

  const [image, setImage] = useState("");

  function submitPhoto(e){
    e.preventDefault();
    Promise.resolve(setImage(e.target[0].files[0]))
      .then(() => {

        fetch("http://localhost:3001/image", {
            method: "POST",
            headers: {
              "Content-Type": "image/png"
            },
            body: image
          }).then((data) => {
            e.target[0].value = null;
            return data.json();
          }).then((res) => {
            console.log(res);
            let {mostFrequentColors, totalPixelOccurences} = res;
            console.log(mostFrequentColors, totalPixelOccurences);
          });
      });

  }

  return (
    <>
      <Routes>
        <Route path = "/" element = {<PhotoForm submitPhoto = {submitPhoto} image = {image}/>}/>
        <Route path = "/photo" element = {<PhotoView/>}/>
      </Routes>
    </>
  );
}

export default App;
