import logo from './logo.svg';
import './App.css';
import {Routes, Route} from "react-router-dom";

import PhotoForm from "./pages/PhotoForm";
import PhotoView from "./pages/PhotoView";

function submitPhoto(e){
  e.preventDefault();

  fetch("http://localhost:3001/image", {
      method: "POST",
      headers: {
        "Content-Type": "image/png"
      },
      body: e.target[0].files[0]
    }).then((data) => {
      e.target[0].value = null;
      return data.json();
    }).then((res) => {
      console.log(res);
      let {mostFrequentColors, totalPixelOccurences} = res.data.mostFrequentColors;
      console.log(mostFrequentColors, totalPixelOccurences);
    });
}

function App() {
  return (
    <>
      <Routes>
        <Route path = "/" element = {<PhotoForm submitPhoto = {submitPhoto}/>}/>
        <Route path = "/photo" element = {<PhotoView/>}/>
      </Routes>
    </>
  );
}

export default App;
